import React, { useEffect, useState } from 'react'

export default function UseEffectMistakes2() {

  const Timer = () => {
    const [count, setCount] = useState(0);

    useEffect(() => {

      const interval = setInterval(() => {
        setCount(prevCount => prevCount + 1)
      }, 1000)

    }, [count])
}

  return (
    <div>
     <h1>Count</h1>
     <p>{count}</p>
    </div>

  )
}
