import React, { useState, useEffect } from "react";

export const useFetch = (options: any) => {
  const [data, setData] = useState(null);

  useEffect(() => {

    if (options.url) {
      fetch(options.url)
        .then((response) => response.json())
        .then((json) => setData(json));
    }
  }, [options.url]);

  return {
    data,
  };
};
