import React, { useEffect, useState } from "react";

const Timer = () => {
  const [count, setCount] = useState(0);

  useEffect(() => {

    const timer = setInterval(() => {

      setCount(prev => prev + 1);
    }, 1000)

    return () => {
      clearInterval(timer)
    }

  }, [count])

  return (
  <div>Timer: {count}</div>
  )
};

export default function UseEffectMistakes() {
  return (
    <>
    <Timer />
    </>
  )
}
